  Stoppable(_curator)
  PullPayment() {}

function bid() external stopInEmergency {
    if (msg.value <= highestBid) throw;

    if (highestBidder != 0) {
          asyncSend(highestBidder, highestBid);
    }

    highestBidder = msg.sender;
    highestBid = msg.value;
}

function withdraw() onlyInEmergency {
    suicide(curator);
  }
}
