    hash = value;
    build_hash_cache();
  }

  static bool match_hash(uint32_t to_check, uint32_t bits, uint32_t match){
    return (match & ~((~0)<<bits)) == (to_check & ~((~0)<<bits));
  }

  bool match(uint32_t bits, uint32_t match) const{
    return match_hash(hash, bits, match);
  }

